import {cloneDeep} from 'lodash';

import {getLeadsTo} from './dataTools.js';
import {orderDisplayed}  from '../libs/orderDisplayed.js';
import {setupArcs} from '../libs/setupArcs.js';

export function setupData(data, nonTechnologies, installment, types) {

  const sortedData = createUnlocks(data, nonTechnologies, installment, types);

  const orderedData = orderDisplayed(sortedData, installment);

  const setupArc = setupArcs(orderedData);






  return setupArc;
}

function createUnlocks(data, nonTechnologies, installment, types) {
  let sortedData = cloneDeep(data);
  let displayed = [];
  let unlocksList = [];

  // First, arrange the technologies by cost
  if (installment > 3) {
    sortedData.technologies.sort((a, b) => b.cost - a.cost);
  }

  // Scoop up all the things each technology leads to and put it in the unlocks object
  sortedData.technologies.forEach((d) => {
    let uniqueUnlocks = [];
    let unlocks = [];

    performance.mark('mark1');
    d.cat = 'technologies';
    displayed.push(d);

    [
      'buildings',
      'build',
      'civics',
      'projects',
      'promotions',
      'religions',
      'resources'
    ].forEach((type) => {
      if (sortedData[type]) {
        unlocks = unlocks.concat(getLeadsTo(d, sortedData[type]));
      }
    });

    performance.mark('mark2');

    if (d.special) {
      d.special.forEach((s) => {
        s.cat = 'specials';
        s.requires = [];
        s.requires.push(d.id);
        unlocks.push(s);
      });
    }

    performance.mark('mark3');

    unlocks.forEach((u, j) => {
      let matchFound = 0;
      unlocksList.forEach((l, k) => {
        if (u.id === l.id) {
          matchFound = 1;
        }
      });
      if (matchFound !== 1) {
          uniqueUnlocks.push(u);
      }
    });
    unlocksList = unlocksList.concat(uniqueUnlocks);

    performance.mark('mark4');

    let unlocksHandler = [];
    uniqueUnlocks.forEach((u, j) => {
      let unlocksItem = {};
      unlocksItem.rank = j;
      unlocksItem.ref = u;
      unlocksHandler.push(unlocksItem);
    });
    d.unlocks = unlocksHandler;

    performance.mark('mark5');

    performance.measure("measure createUnlocks", 'mark1', 'mark2');
    performance.measure("measure orderDisplayed", 'mark2', 'mark3');
    performance.measure("measure setupArcs", 'mark3', 'mark4');
    performance.measure("measure setupArcs", 'mark4', 'mark5');
    console.log(performance.getEntriesByType("measure"));
  });

  sortedData.displayed = displayed;

  // Label data categories
  types.forEach((t) => {
    if (sortedData[t]) {
      sortedData[t].forEach((d) => {
        d.cat = t;
      });
    }
  });

  return sortedData;
}
